import React, { useState, useRef, useEffect } from 'react';
import './App.css';

const API_ENDPOINT = 'https://ctq7xfkhg6.execute-api.us-east-1.amazonaws.com/prod/chat';

function App() {
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: "👋 Hi! I'm Smart Study Buddy, your AI tutor powered by AWS Bedrock Agent.\n\nI can help you learn any topic with:\n• 🔍 Real-time web search\n• 📰 Latest news & updates\n• 🌐 Bilingual support (English/Spanish)\n• 🖼️ Image analysis\n\nJust ask me anything!",
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sessionId] = useState('session-' + Date.now());
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const suggestedPrompts = [
    { text: "🔬 Quantum Computing" },
    { text: "📰 Latest AI News" },
    { text: "🧠 Redes Neuronales (ES)" }
  ];

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    const userMessage = {
      role: 'user',
      content: input,
      timestamp: new Date()
    };

    const userInput = input; // Save input before clearing
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      console.log('Sending request to:', API_ENDPOINT);
      console.log('Message:', userInput);

      const response = await fetch(API_ENDPOINT, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          message: userInput,
          sessionId: sessionId
        })
      });

      console.log('Response status:', response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Response error:', errorText);
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('API Response:', data);

      const assistantMessage = {
        role: 'assistant',
        content: data.response || 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date()
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Error details:', error);
      const errorMessage = {
        role: 'assistant',
        content: `Connection error: ${error.message}. Please check the console for details.`,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handlePromptClick = (prompt) => {
    setInput(prompt.text);
  };

  return (
    <div className="app">
      <div className="header">
        <div className="header-content">
          <div className="logo">🧠</div>
          <div>
            <h1>Smart Study Buddy</h1>
            <p>Your autonomous AI tutor powered by AWS Bedrock Agent</p>
          </div>
        </div>
      </div>

      <div className="features-banner">
        <div className="features-content">
          <div className="feature-item">
            <span className="status-dot"></span>
            <span>Built with <strong>AWS Bedrock AgentCore + Claude 3.5 Sonnet</strong></span>
          </div>
          <div className="feature-item">
            <span>🤖 <strong>Autonomous:</strong> I decide when to search web or fetch news!</span>
          </div>
          <div className="feature-item">
            <span>🌐 <strong>Bilingual:</strong> Automatically responds in English or Spanish!</span>
          </div>
        </div>
      </div>

      <div className="container">
        <div className="chat-container">
          <div className="messages-container">
            {messages.map((message, index) => (
              <div key={index} className={`message ${message.role}`}>
                <div className="avatar">
                  {message.role === 'user' ? '👤' : '🧠'}
                </div>
                <div className="message-content">
                  <div className="message-bubble">
                    {message.content}
                  </div>
                  <div className="message-time">
                    {message.timestamp.toLocaleTimeString()}
                  </div>
                </div>
              </div>
            ))}

            {isLoading && (
              <div className="message assistant">
                <div className="avatar">🧠</div>
                <div className="message-content">
                  <div className="thinking-indicator">
                    <div className="thinking-dots">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                    <span>Thinking...</span>
                  </div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>

          {messages.length === 1 && (
            <div className="suggested-prompts">
              <p className="prompts-label">💡 Try these:</p>
              <div className="prompts-grid">
                {suggestedPrompts.map((prompt, index) => (
                  <button
                    key={index}
                    onClick={() => handlePromptClick(prompt)}
                    className="prompt-button"
                  >
                    {prompt.text}
                  </button>
                ))}
              </div>
            </div>
          )}

          <div className="input-container">
            <div className="input-wrapper">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleSend()}
                placeholder="Ask me anything..."
                className="input-field"
                disabled={isLoading}
              />
              <button
                onClick={handleSend}
                disabled={isLoading || !input.trim()}
                className="send-button"
              >
                {isLoading ? 'Thinking...' : 'Send'} ➤
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="footer">
        © 2025 — Built with <strong>AWS Bedrock Agent</strong> · <strong>React</strong> · <strong>Autonomous AI</strong>
      </div>
    </div>
  );
}

export default App;
